const menu_open_btn=document.getElementById("header__menu-button"),menu_close_btn=document.getElementById("menu__close-button"),menu=document.getElementById("menu"),menu_links=document.getElementsByClassName("menu__link"),showMenu=()=>menu.style.transform="translateX(0)",closeMenu=()=>menu.style.transform="translateX(100%)",enableMenu=()=>{if(window.outerWidth<768)for(item of(menu.style.transform="translateX(100%)",menu.addEventListener("click",closeMenu),menu_open_btn.addEventListener("click",showMenu),menu_close_btn.addEventListener("click",closeMenu),menu_links))item.addEventListener("click",closeMenu);else{for(item of(menu.removeEventListener("click",closeMenu),menu_open_btn.removeEventListener("click",showMenu),menu_close_btn.removeEventListener("click",closeMenu),menu_links))item.removeEventListener("click",closeMenu);menu.style.transform="translateX(0)"}};window.addEventListener("load",enableMenu),window.addEventListener("resize",enableMenu);